import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

pragma solidity ^0.8.0;

contract Validator is AccessControlUpgradeable, PausableUpgradeable {

    string public constant name = "Contractful";
    mapping(uint256 => bool) public usedNonces;
    bytes32 public DOMAIN_SEPARATOR;

    function initialize_() public onlyInitializing() {
        uint256 chainId;
        assembly {
            chainId := chainid()
        }

        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256(
                    "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
                ),
                keccak256(bytes(name)),
                keccak256(bytes("1")),
                chainId,
                address(this)
            )
        );
    }

    /**
    * @notice checks whether the signer signed the description
     */
    function validateDescription(
        address signer,
        uint8 v,
        bytes32 r,
        bytes32 s,
        string calldata description,
        uint256 nonce
    ) public returns (bool) {
        require(!usedNonces[nonce]);
        usedNonces[nonce] = true;

        bytes32 message = keccak256(
            abi.encode(
                keccak256(
                    "Signature(string description, uint256 nonce)"
                ),
                description,
                nonce
            )
        );

        bytes32 digest = keccak256(
            abi.encodePacked("\x19\x01", DOMAIN_SEPARATOR, message)
        );

        address recovered = ecrecover(digest, v, r, s);
        return signer != address(0) && signer == recovered;
    }
}","keccak256":"0xed818af29cefe233de39ada7613605a6bea50535f5d8ddf8e44702c968a29947","license":"MIT"},"src/lib/Errors.sol":{"content":"//SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

library Errors {
  // *** Contract Specific Errors ***
  // Manager Contract 
  error MG_AGREEMENT_CLOSED(); // "The agreement is closed"
  error MG_AGREEMENT_INACTIVE(); // "The agreement is inactive. This might because the agreement has not been activated or because it has been closed"
  error MG_AGREEMENT_NOT_PENDING(); // "The agreement is not pending. It can be active or closed"
  error MG_UNAUTHORIZED(); // "msg.sender lacks the authorizations required"
  error MG_CONTRACTOR_EQUALS_CONTRACTEE(); // "The contractor and the contractee cannot be the same"
  error MG_INVALID_MATURITY_DATE(); // "The maturity date must be in the future"
  error MG_INVALID_TOKEN(); // "The token is not an accepted token. It probably is a zero address"
  error MG_ADDRESS_ZERO(); // "The address cannot be zero"
  error MG_INVALID_MIGRATION_PERIOD(); // "The migration period is not valid"
  error MG_AMOUNT_TOO_HIGH(); // "The amount is too high"
  error MG_FUNDS_ALREADY_SECURED(); // "The funds are already secured"
  error MG_ACCEPTANCE_PERIOD_EXPIRED(); // "The acceptance period has expired"
}
